function cardListItems(aList){return`\n  ${aList.map(list=>`<li>${list}</li>`).join("")}\n  `}function cardTemplate(aCard){return`\n  <div class="card">\n    <i class="${aCard.icon}"></i>\n    <h3><a href="${aCard.link}" title="${aCard.linkTitle}">${aCard.title}</a></h3>\n    <hr>\n    <ul>\n    ${cardListItems(aCard.list)}\n    </ul>\n  </div>\n  `}var container=document.getElementById("cards"),cardsData=new XMLHttpRequest;cardsData.open("GET","assets/json/cards-data.json"),cardsData.onload=function(){if(cardsData.status>=200&&cardsData.status<400){var aData=JSON.parse(cardsData.responseText);container.innerHTML=`${aData.map(cardTemplate).join("")}`}else container.innerHTML='<p class="alert-error">La connexion au serveur a réussie mais la récupération des données a échoué.</p>'},cardsData.onerror=function(){container.innerHTML='<p class="alert-error">La connexion au serveur a échouée.</p>'},cardsData.send();